<?php

namespace Tests\App\Core\Component\Message\Domain\Aggregate;

use App\Core\Component\Message\Domain\Aggregate\Message;
use App\Core\Component\Message\Domain\Exception\InvalidMessageException;
use PHPUnit\Framework\TestCase;

/**
 * @covers \App\Core\Component\Message\Domain\Aggregate\Bottle
 *
 * @internal
 */
class MessageTest extends TestCase
{
    public function testItShouldCreateAMessage(): void
    {
        $message = Message::create('Hello!');

        self::assertSame('Hello!', $message->getContent());
    }

    public function testItShouldThrowExceptionOnLengthLimitExceeded(): void
    {
        self::expectException(InvalidMessageException::class);
        Message::create(' Message.');
    }

    /**
     * @dataProvider emptyMessageProvider
     */
    public function testItShouldThrowExceptionOnEmptyContent(string $emptyMessage): void
    {
        self::expectException(InvalidMessageException::class);
        Message::create($emptyMessage);
    }

    public function emptyMessageProvider(): iterable
    {
        yield 'empty message case' => [''];
        yield 'empty message with only spaces case' => ['   '];
    }
}
